import React,{Component} from "react"
import Item from "./item"

class Items extends Component{

    state={
        items : [
            {id:0,value:0},
            {id:1,value:0},
            {id:2,value:0}
        ]
    }

    render(){
        return(
            <React.Fragment>
                {this.state.items.map(item=>(<Item key={item.id}/>))}
            </React.Fragment>
        )
    }
}

export default Items;
