import React,{Component} from "react"
import Item from "./item"

class Items extends Component{


    render(){
        return(
            <React.Fragment>
                <button>
                    Reset All
                </button>

                {this.state.items.map(item=>(
                                            <Item  key={item.id} 
                                                   onDelete={this.props.handleDelete}
                                                   increment={this.props.handleIncreament}
                                                   item={item}
                                            />

                                            ))
                }
            </React.Fragment>
        )
    }
}

export default Items;
