import React,{Component} from "react"
import Item from "./item"

class Items extends Component{
    render(){
        return(
            <React.Fragment>
                <button onClick={this.props.handleReset} className = "btn btn-success btn-lg m-3">
                    Reset All
                </button>
                <h1>
                    {console.log("stuff in the box"+this.props.Component)}
                </h1>
                {this.props.items.map(item=>(
                                            <Item  key={item.id} 
                                                   onDelete={this.props.handleDelete}
                                                   increment={this.props.handleIncreament}
                                                   item={item}
                                            />  
                                            ))
                }
            </React.Fragment>
        )
    }
}

export default Items;
